mapper.insert:
INSERT INTO public.cart(
    cart_amt
    , pdct_no
    , mem_no
    , reg_date
)VALUES(
    #{cart_amt}
    , Cast(#{pdct_no} AS INTEGER)
    , #{mem_no}
    , current_date
)

mapper.update:
update cart
set
cart_amt = #{cart_amt}
, chg_date = current_date
where mem_no = #{mem_no}
and pdct_no = Cast(#{pdct_no} AS INTEGER)
